import React, { PureComponent } from 'react'
import { feature } from 'topojson'
import { easeQuadOut } from 'd3-ease'
import { Animate } from 'react-move'
import { interpolate } from 'flubber'
import countries from '../../data/geo_country';

const view = [100, 100] // [width, height]
const trbl = [10, 10, 10, 10] // [top, right, bottom, left] margins

class Example extends PureComponent {
  state = {
    states: countries.data.features.map(d => {
        return d.geometry.coordinates[0]
    }),
    // states: [ [[10, 50], [20, 2], [30, 2]],[[0, 0], [2, 0], [2, 1], [1, 2], [0, 1]] ],
  }

  update = () => { // take the first one, put it at the end
    this.setState(({ states }) => ({
      states: [
        ...states.slice(1),
        states[0],
      ],
    }))
  }

  render() {
      console.log(this.props);
    const { update, state: { states } } = this;
    const interpolator = interpolate(states[0], states[1]);
    console.log(interpolate(states[0], states[1]));
    return (
      <div>
        <button onClick={update}>Update</button>
        <svg view={view} trbl={trbl}>
          <Animate
            start={{
              opacity: 0,
              d: interpolator(1),
            }}

            enter={[
              {
                opacity: [0.7],
                timing: { duration: 1000 },
              },
            ]}

            update={{
              d: interpolator,
              timing: { duration: 1000, ease: easeQuadOut },
            }}
          >
            {(state) => {
              return (
                <g transform="translate(100, 0) scale(0.8)">
                  <path
                    fill="#ff69b4"
                    {...state}
                  />
                </g>
              )
            }}
          </Animate>
        </svg>
      </div>
    )
  }
}

export default Example
