import React from 'react';
import classes from './Visualisation.module.css';
import LineGraph from './LineGraph/LineGraph';
import LineChart from './LineChart/LineChart';
import KeyFigures from '../KeyFigures/KeyFigures';

const visualisation = props => {
    const { 
        keyFigureList, 
        language, data,
        query, history } = props;

    let country = history.location.pathname === '/fr' ? 'Country_FR' : 'Country',
    dataType = keyFigureList.filter(d => d.type === query.keyfigure );

    let filterData = {
        country: country,
        data: cleanData(data[dataType[0].label]),
        type: dataType[0].label
    }

    function cleanData(data) {
        let res = [];
        for (let i in data) {
            res.push({ 
                year: i, 
                value: dataType[0].ID === 2 ? data[i] * 100 : dataType[0].ID === 4 ? data[i] * 100 : data[i],
                yAxisKey: dataType[0].ID === 2 ? data[i] * 100 : dataType[0].ID === 4 ? data[i] * 100 : parseFloat(data[i]).toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ")
            })
        }
        return res;
    }

    let line = <LineGraph 
        data={filterData}
        query={query}
    />

    return (
        <div className={classes.Visualisation}> 
            <KeyFigures 
                language={language}
                query={query}
                data={keyFigureList}             
                onKeyFigureChange={props.onKeyFigureChange}
             />
             {line}
        </div>
    )
};

export default visualisation;
