        //                 onChange={onKeyFigureChange}
        //             />
        //             <label>
        //                 {d[lang]}   
        //             </label> 
        //         </React.Fragment>
        //     ))}
        // </form> 
    );
};

const styles__sm = {
  // indicatorSeparator: (base, state) => {

  // },
  menu: (base, state) => {
      return {
          ...base,
          backgroundColor: "#fef7e7",
          borderRadius: 0,
          boxShadow: 0,
      }
  },
  option: (base, { isDisabled, isFocused, isSelected }) => {
    return {
      ...base,
      backgroundColor: isDisabled ? "#a70000" : isSelected ? "#a70000" : null,
      ':active': {
      },
    }
  },
  menuList: (base, state) => {
      return {
          ...base,
      }
  },
  control: (base, state) => {
    return { ...base,
      isHidden: true, 
      boxShadow: 'none',
      // backgroundColor: 'none',
      borderStyle: 'solid', 
      borderColor: '#449999',
      borderWidth: '0px',
      color: 'none', 
      "&:hover": { 
      }
    };
  },
  singleValue: (base, state) => {
    return {
      ...base,
      // color: '#212529',
      // textAlign: "right",
    }
  },
  input: (base, state) => {
    return {
      ...base,
      // color: '#212529'
    }
  }
};

