import React from 'react';
import classes from './SearchBar.module.css';
import Select from 'react-select';

const searchBar = props => {

    const {
        options,
        query,
    } = props;

    return (
        <div className={classes.SearchBar}>
            <Select 
                styles={styles}
                onChange={props.onSearchChange}
                menuIsOpen={false}
                options={options}
                value={options[query.country-1]}
                components={props.menuIsOpen === true ? {Control:  () => null} : null }
            />
        </div>
    )
}

const styles = {
    menu: (base, state) => {
        return {
            ...base,
            backgroundColor: "#fef7e7",
            borderRadius: 0,
            boxShadow: 0,
            height: '572px',
            // opacity: ".9"
        }
    },
    option: (base, { isDisabled, isFocused, isSelected }) => {
      return {
        ...base,
        backgroundColor: isDisabled ? "#a70000" : isSelected ? "#a70000" : null,
        ':active': {
        },
      }
    },
    menuList: (base, state) => {
        return {
            ...base,
            maxHeight: 'none',
            height: '100%',
        }
    },
    dropdownIndicator: (base, state) => {
      return { ...base,

      };
    },
    control: (base, state) => {
      return { ...base,
        isHidden: true, 
        boxShadow: 'none',
        backgroundColor: 'none',
        borderStyle: 'solid', 
        borderColor: '#449999',
        borderWidth: '0px',
        color: 'none', 
        "&:hover": { 
        }
      };
    },
    singleValue: (base, state) => {
      return {
        ...base,
        fontSize:'2rem',
        fontWeight:'600',
        color: '#212529',
      }
    },
    input: (base, state) => {
      return {
        ...base,
        fontSize:'2rem',
        fontWeight:'900',
        color: '#212529'
      }
    }
};

export default searchBar
