import React from 'react';
import classes from './KeyFigures.module.css';
import Select, { components } from 'react-select'; 

export default props => {

    const { data, query, onKeyFigureChange } = props;
    let lang = 'type', description = 'description';
    if ( props.language === 'fr') {
      lang = 'type_FR'
      description = 'description_FR'
    }

    let checkValue = data.filter(d => d.type === query.keyfigure)
    let options = data.map(d => ({
        value: d.ID, label: d[lang], description: d[description]
    }))
    let style = props.type === 'small' ? styles__sm : styles;

    return (
        <div className={classes.KeyFiguresWrapper}>
            <Select 
                options={options}
                styles={style}
                className={classes.KeyFigures}
                isSearchable={false}
                value={options[checkValue[0].ID - 1]}
                onChange={onKeyFigureChange}
                // components={
                    // props.type ==='small' ? null : {IndicatorSeparator: null},
                // }
                components={{
                  // IndicatorSeparator: null,
                  IndicatorsContainer: IndicatorsContainer
                }}
            />
        </div>
    );
};

const IndicatorsContainer = props => {
  console.log(props.selectProps.value.description);
  return (
    <div className={classes.Control}>
      <div className={classes.ControlInfo}>
        <i className={classes["material-icons"]}> info </i>
        <div className={classes.InfoTooltip}>
            The Aid Globe estimates SDG financing based on providers’ activities (ODA, USD Disbursement, 2017). Activities can be classified to zero, one, or multiple SDGs, varying by provider.<br/>Learn more <a href="https://sdg-financing-lab.oecd.org/about/#FAQs" target='_blank' rel='noopener noreferrer'>here.</a>
        </div>
      </div>
      <components.IndicatorsContainer {...props} />
    </div>
  )
}

const styles__sm = {
  indicatorSeparator: (base, state) => {

  },
  menu: (base, state) => {
      return {
          ...base,
          backgroundColor: "white",
          borderRadius: 0,
          boxShadow: 0,
      }
  },
  option: (base, { isDisabled, isFocused, isSelected }) => {
    return {
      ...base,
      backgroundColor: isDisabled ? "#a70000" : isSelected ? "#a70000" : null,
      ':active': {
      },
    }
  },
  menuList: (base, state) => {
      return {
          ...base,
      }
  },
  control: (base, state) => {
    return { ...base,
      isHidden: true, 
      boxShadow: 'none',
      backgroundColor: 'none',
      borderStyle: 'solid', 
      borderBottom: '2px solid black',
      borderRadius: '0px',
      borderWidth: '0px',
      color: 'none', 
      "&:hover": { 
      }
    };
  },
  singleValue: (base, state) => {
    return {
      ...base,
      // color: '#212529',
      // textAlign: "right",
    }
  },
  input: (base, state) => {
    return {
      ...base,
      // color: '#212529'
    }
  }
};

